package week1;

public class DemoOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10;
		int num2=20;
		int result;
		
		result=num1+num2;
		System.out.println("The result for addition "+result);
		result=result-num1;
		System.out.println("The result for substraction is "+result);
		result=num1*num2;
		System.out.println("The result for multiplication is "+result);
		result=num1/num2;
		System.out.println("The result for division is "+result);
		
	}

}
